import React, { useState } from "react";
import Checklist from "./components/Checklist";
import "./App.css";

function App() {
  const [submitted, setSubmitted] = useState(false);
  const [form, setForm] = useState({
    nome: "",
    cpf: "",
    email: "",
    telefone: "",
    descricao: "",
  });

  // Atualiza o estado quando o usu√°rio digita
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Ao enviar
  const handleSubmit = (e) => {
    e.preventDefault();

    // Salva no localStorage
    const listaExistente = JSON.parse(localStorage.getItem("denuncias")) || [];
    listaExistente.push({ ...form, data: new Date().toISOString() });
    localStorage.setItem("denuncias", JSON.stringify(listaExistente));

    // Limpa o formul√°rio
    setForm({ nome: "", cpf: "", email: "", telefone: "", descricao: "" });

    // Mostra checklist
    setSubmitted(true);
  };

  return (
    <div className="container">
      {!submitted ? (
        <>
          <header className="header">
            <div className="logo">üèõÔ∏è <span>√ìrg√£o Institucional</span></div>
          </header>

          <h1>Portal de Den√∫ncias Institucionais</h1>
          <p>Preencha o formul√°rio abaixo para enviar sua den√∫ncia an√¥nima.</p>

          <form onSubmit={handleSubmit} className="formulario" autoComplete="off">
            <label>Nome completo:</label>
            <input name="nome" type="text" value={form.nome} onChange={handleChange} required />

            <label>CPF:</label>
            <input name="cpf" type="text" value={form.cpf} onChange={handleChange} required />

            <label>E-mail:</label>
            <input name="email" type="email" value={form.email} onChange={handleChange} required />

            <label>Telefone:</label>
            <input name="telefone" type="tel" value={form.telefone} onChange={handleChange} required placeholder="(99) 99999-9999" />

            <label>Descri√ß√£o da den√∫ncia:</label>
            <textarea name="descricao" value={form.descricao} onChange={handleChange} required></textarea>

            <button type="submit">Enviar den√∫ncia</button>
          </form>
        </>
      ) : (
        <Checklist />
      )}

      <footer className="footer">
        <small>Projeto educativo ‚Äî n√£o envie dados reais.</small>
      </footer>
    </div>
  );
}

export default App;
