import React, { useState } from "react";
import Checklist from "./components/Checklist";
import "./App.css";

function App() {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    const form = e.target; // o pr√≥prio form
    const dados = {
      nome: form.nome.value,
      cpf: form.cpf.value,
      email: form.email.value,
      descricao: form.descricao.value,
      data: new Date().toISOString(), // opcional, para saber quando foi enviado
    };

    // üîπ Pegar dados j√° salvos no localStorage
    const listaExistente = JSON.parse(localStorage.getItem("denuncias")) || [];
