    // üîπ Salvar novamente no localStorage
    localStorage.setItem("denuncias", JSON.stringify(listaExistente));

    // Limpar campos do formul√°rio (opcional)
    form.reset();

    // Mostrar checklist
    setSubmitted(true);
  };

  return (
    <div className="container">
      {!submitted ? (
        <>
          <header className="header">
            <div className="logo">
              üèõÔ∏è <span>SENAI</span>
